import React, { Component } from "react";
// This will require to npm install axios
import axios from 'axios';
import { Link } from "react-router-dom";



var leftPlayer = []
var rightPlayer = []
var players = []

export default class Vote extends Component {
  // This is the constructor that shall store our data retrieved from the database
  constructor(props) {
    super(props);
    this.state = { records: [] };
  }

  // This method will get the data from the database.
  componentDidMount() {
    axios
      .get("http://localhost:3000/record/")
      .then((response) => {
        this.setState({ records: response.data });
        players = response.data;
        var left = Math.floor(Math.random() * (response.data.length + 1));
        var right = Math.floor(Math.random() * (response.data.length + 1));
        while(right == left){
            right = Math.floor(Math.random() * (response.data.length + 1));
        }
        leftPlayer=response.data[left];
        rightPlayer=response.data[right];
        document.getElementById("left").innerHTML = leftPlayer.playerName;
        document.getElementById("right").innerHTML = rightPlayer.playerName;
      })
      .catch(function (error) {
        console.log(error);
      });
  }


   leftClick(){
    console.log('clicked left player', leftPlayer)
    axios({
        method: 'post',
        url: 'http://localhost:3000/update',
        data: {
          '_id': String(leftPlayer._id),
          'playerName': leftPlayer.playerName,
          'Elo': (parseInt(leftPlayer.Elo) + 100)
        }
      }).then((response) => {
        console.log(response);
      });
    }

    rightClick(){
        console.log('clicked right player', rightPlayer)
        axios({
            method: 'post',
            url: 'http://localhost:3000/update',
            data: {
              '_id': String(rightPlayer._id),
              'playerName': rightPlayer.playerName,
              'Elo': (parseInt(rightPlayer.Elo) + 100)
            }
          }).then((response) => {
            console.log(response);
          });
          this.Undecided()
    }

    Undecided(){
        var left = Math.floor(Math.random() * (players.length + 1));
        var right = Math.floor(Math.random() * (players.length + 1));
        while(right == left){
            right = Math.floor(Math.random() * (this.state.records.length + 1));
        }
        leftPlayer= players[left];
        rightPlayer= players[right];
        document.getElementById("left").innerHTML = leftPlayer.playerName;
        document.getElementById("right").innerHTML = rightPlayer.playerName;
      
    }


  // This following section will display the table with the records of individuals.
  render() {
    return (
      <div>
        <button id="left" class="btn btn-primary" onClick={this.leftClick}>
          
        </button>
        <button id="right" class="btn btn-primary" onClick={this.rightClick}>
          
        </button>
        <button class="btn btn-primary" onClick={this.Undecided}>
          Undecided
        </button>
      </div>
    );
  }
}
