      console.log(leftPlayer.playerName)
  }

  rightClick(){
    console.log(rightPlayer.playerName)
}

  // This following section will display the table with the records of individuals.
  render() {
    return (
      <div>
        <button class="btn btn-primary" onClick={this.leftClick}>
          {leftPlayer.playerName}
        </button>
        <button class="btn btn-primary" onClick={this.rightClick}>
          {rightPlayer.playerName}
        </button>
        <button class="btn btn-primary">
          Undecided
        </button>
      </div>
    );
  }
}
